import React, { Component } from 'react';

class Lyrics extends Component {
    state = {
        track: {},
        lyric: {}
    }

    componentDidMount() {
        axios.get(`https://api.happi.dev/v1/music/artists/:id_artist/albums/:id_album/tracks/:id_track/lyrics


        https://api.happi.dev/v1/music?q=Freedom&limit=10&apikey=${process.env.REACT_APP_MM_KEY}&type=`)
            .then(res => {
                console.log(res.data);
                this.setState({result: res.data.result});
            })
            .catch(err => console.log(err));
    }

    render() {
        return (
            <div>
                <h1>Lyrics</h1>
            </div>
        )
    }
}

export default Lyrics;
