import React, { Component } from 'react'
import axios from 'axios'
import {Consumer} from '../../context'

export default class Search extends Component {
    state ={ trackTitle: ''
}
    render() {
        return (
           <Consumer>
           {value => {
               return (
                    <div className="card card-body mb-4 p-4"> 
                        <h2 className="display-4 text-center">
                        <i className="fa fa-search"> </i> Search for Music & Lyrics
                    </h2>
                    <p className="lead text-center">Get the Track Info and Lyrics for any Song</p>
                    <form>
                        <div className="form-group">
                            <input type='text' className="form-control form-control-lg" placeholder="Search for music ..."></input>
                        </div>    
                    </form>

                </div>
               );
           }}
           </Consumer>
        )
    }
}
