import React, { Component } from 'react'
import axios from 'axios'
import {Consumer} from '../../context'

export default class Search extends Component {
    state ={ trackTitle: ''
    };

    findTrack =(e) => {
        axios.get(`https://api.happi.dev/v1/music?q=${this.state.trackTitle}&limit=50&apikey=${process.env.REACT_APP_MM_KEY}&type=`
            )
            .then(res => {
                console.log(res.data);
            })
            .catch(err => console.log(err));
    }     
    onChange = e => {
        this.setState({[e.target.name]: e.target.value});
    }
    render() {
        return (
           <Consumer>
           {value => {
               return (
                    <div className="card card-body mb-4 p-4"> 
                        <h2 className="display-4 text-center">
                        <i className="fa fa-search"> </i> Search for Music & Lyrics
                    </h2>
                    <p className="lead text-center">Get the Track Info and Lyrics for any Song</p>
                    <form onSubmit={this.findTracks}>
                        <div className="form-group">
                            <input type='text' 
                            className="form-control form-control-lg" 
                            placeholder="Search for song by keywords in title ..."
                            name="trackTitle"
                            value={this.state.trackTitle}
                            onChange={this.onChange}
                            />
                        </div>   
                        <button className="btn btn-warning btn-lg btn-block mb-5">Search</button> 
                    </form>

                </div>
               );
           }}
           </Consumer>
        )
    }
}
